mapper.select:
SELECT sacbi.id, catalogue, country, ExtractValue ( c.fullname, "//value[@lang='zh_CN']" ) guojia, dynasty, (SELECT dicvalue FROM sys_dictionary_basic_data_info WHERE dickey = dynasty AND dictype = "CHINESE_DYNASTY") dynastyname, fullsubject, shortsubject, commonlysubject, castingtime, facevaluedis, material, (SELECT ExtractValue ( m.fullname, "//value[@lang='zh_CN']" ) FROM sys_coin_material m WHERE m.id = material) caizhi, grade, (SELECT dicvalue FROM sys_dictionary_basic_data_info WHERE dickey = grade AND dictype = "ACT_C_GRADE") gradename, (SELECT money FROM sys_box_basic_info box WHERE boxid=box.id) as boxCost, weight, size, version, evaluate, remarks, CONCAT_WS( c.shortname, (SELECT dicvalue FROM sys_dictionary_basic_data_info WHERE dickey = dynasty AND dictype = "CHINESE_DYNASTY"), fullsubject, shortsubject, castingtime, commonlysubject, shortsubject, facevaluedis, VERSION ) FROM sys_ancient_coin_basic_info sacbi, sys_country c WHERE c.id = sacbi.country
and sacbi.dynasty=#{dynasty}
and sacbi.dynasty=#{dynasty}
and sacbi.country =#{country}
and sacbi.facevaluedis like CONCAT("%",#{facevaluedis},"%")
and sacbi.material =#{material}
and sacbi.version like CONCAT("%",#{version},"%")
and sacbi.size like CONCAT("%",#{size},"%")
and CONCAT_WS( c.shortname, (SELECT dicvalue FROM sys_dictionary_basic_data_info WHERE dickey = dynasty AND dictype = "CHINESE_DYNASTY"), fullsubject, shortsubject, castingtime, commonlysubject, shortsubject, facevaluedis, version ) like CONCAT("%",#{item},"%")
limit #{startRow},15
