AND entitytype = #{entityType,jdbcType=VARCHAR}
and entitytype is null
and entitytype is not null
) )t LEFT JOIN employee e ON t.ownid = e.id

SELECT t.goodstype, t.goodsno, t.ownid, t.requeststatus, t.goodsstatus, e.name AS ownname, t.sendtype, t.handlingtime, t.paydiffertime, t.payinfo, t.duetime, t.coinnum FROM (SELECT '申请单' AS goodstype, rr.requestcode AS goodsno, rr.ownid AS ownid, rr.requeststatus AS requeststatus, rr.internalstates AS goodsstatus, rr.sendtype AS sendtype, rr.requesttime AS handlingtime, rr.duetime AS duetime, TIME_TO_SEC( TIMEDIFF( (SELECT DATE_FORMAT( MAX(p.createdate), '%Y-%m-%d %H:%i:%S' ) FROM pay_journal p WHERE FIND_IN_SET(p.requestcode, rr.requestcode)), rr.duetime ) ) * (- 1) AS paydiffertime, ( CASE WHEN rr.paystatus = '1' THEN IFNULL( (SELECT DATE_FORMAT( MAX(p.createdate), '%Y-%m-%d %H:%i:%S' ) FROM pay_journal p WHERE FIND_IN_SET(p.requestcode, rr.requestcode)), "已支付" ) ELSE "--" END ) payinfo, (SELECT SUM(r.amount) FROM request_detail r WHERE requestid = rr.id) coinnum FROM request_main rr WHERE rr.internalstates = 3 OR rr.internalstates = 4 AND delflag = '0'
)t LEFT JOIN employee e ON t.ownid = e.id LEFT JOIN request_main r ON r.requestcode=goodsno WHERE 1=1
and r.entitytype is null
and r.entitytype is not null
ORDER BY t.handlingtime ASC limit #{page,jdbcType=INTEGER} , #{pagesize,jdbcType=INTEGER}

