mapper.select:
select
lp.packno,lp.logisticsno,lp.shippername,lp.shippertel,lp.shipperaddress,lp.packstatus,lp.receivestatus,lp.requestcodes,lp.type,lp.customertel,lp.customername,lp.remark,lp.customerno,lp.ownid,lp.createtime,e.`name`
as name,lp.cutstatus
from logistics_pack lp LEFT JOIN employee e ON lp.createby=e.id
WHERE 1=1


	AND lp.packno= #{packno,jdbcType=INTEGER}


	AND lp.logisticsno = #{logisticsno,jdbcType=VARCHAR}


	AND lp.shippername = #{shippername,jdbcType=VARCHAR}


	AND e.name = #{name,jdbcType=VARCHAR}



	AND lp.createtime > #{timestart,jdbcType=TIMESTAMP}


	AND lp.createtime < #{timeend,jdbcType=TIMESTAMP}

ORDER BY lp.createtime DESC
LIMIT #{page,jdbcType=INTEGER} , #{pagesize,jdbcType=INTEGER}

SELECT COUNT(*) FROM logistics_pack lp LEFT JOIN employee e ON
lp.createby=e.id
WHERE 1=1

	AND lp.packno= #{packno,jdbcType=INTEGER}


	AND lp.logisticsno = #{logisticsno,jdbcType=VARCHAR}


	AND lp.shippername = #{shippername,jdbcType=VARCHAR}


	AND e.name = #{name,jdbcType=VARCHAR}



	AND lp.createtime > #{timestart,jdbcType=TIMESTAMP}


	AND lp.createtime < #{timeend,jdbcType=TIMESTAMP}
