mapper.select:
SELECT count(1) FROM reservation_record rr WHERE 1 = 1 AND delflag = '0'
AND userid = #{userid,jdbcType=VARCHAR}
AND reservationtypeid = #{reservationtypeid,jdbcType=VARCHAR}
AND EXISTS ( SELECT 1 FROM request_main r WHERE r.requestcode = rr.requestcode AND r.internalstates != 0 AND requestcode != #{requestcode,jdbcType=INTEGER} ) AND requestcode = #{requestcode,jdbcType=INTEGER}
AND couponid = #{couponid,jdbcType=VARCHAR}
AND (couponid is null or couponid = "") AND EXISTS ( SELECT 1 FROM reservation_type rt WHERE rt.reservationtypeid = rr.reservationtypeid AND rt.needcoupon != 1 )
