mapper.select:
SELECT srr.requestcode, r.customer, r.mobile, srr.resetreason, srr.appreseter, e. NAME AS appresetername, srr.appresettime, srr.dealer, e2. NAME AS dealername, srr.state, srr.ownid, u.defaultmobile, u.defaultmail, r.requeststatus, srr.dealtime FROM request_main r RIGHT JOIN single_receipt_records srr ON r.requestcode = srr.requestcode LEFT JOIN employee e ON e.id = srr.appreseter LEFT JOIN employee e2 ON e2.id = srr.dealer LEFT JOIN USER u ON u.id = r.userid WHERE 1 = 1
and srr.requestcode=#{requestcode,jdbcType=VARCHAR}
and r.mobile like #{guestMobile,jdbcType=VARCHAR}
and u.name = #{guestName,jdbcType=VARCHAR}
and r.usercode= #{guestCode,jdbcType=VARCHAR}
and srr.state= #{dealState,jdbcType=VARCHAR}
order by srr.appresettime desc LIMIT #{page,jdbcType=INTEGER},#{pagesize,jdbcType=INTEGER}

select count(*) from request_main r
right join single_receipt_records srr on
r.requestcode=srr.requestcode
left join employee e on
e.id=srr.appreseter
left join employee e2 on e2.id=srr.dealer
left join
user u on u.id=r.userid
where 1=1

	and srr.requestcode=#{requestcode,jdbcType=VARCHAR}


	and r.mobile like #{guestMobile,jdbcType=VARCHAR}


	and u.name = #{guestName,jdbcType=VARCHAR}


	and r.usercode= #{guestCode,jdbcType=VARCHAR}


	and srr.state= #{dealState,jdbcType=VARCHAR}

mapper.update:
update
single_receipt_records
set ownid = #{operater,jdbcType=INTEGER}
where
requestcode = #{requestcode,jdbcType=INTEGER}
AND state != '3'
