mapper.select:

     select wr.mtype,wr.id,wr.userid,us.usercode,us.name as username,wr.refundno,w.balance as EWallet,wr.type,wr.amount,wr.refunddate,wr.walletid,wr.remark,wr.status,wr.confirmdate,wr.confirmuserid,wr.delflag,wr.capitalid,wr.orderdate,wr.capitalvalue 
	from wallet_refund wr,`user` us,wallet_money w
	where us.id=wr.userid and wr.delflag='0' and w.userid=us.id

		and wr.mtype=#{mtype,jdbcType=INTEGER}


    and wr.userid =#{userid,jdbcType=VARCHAR}


    and wr.amount =#{amount,jdbcType=DECIMAL}


    and wr.type =#{type,jdbcType=CHAR}


    and wr.status =#{status,jdbcType=CHAR}


        and wr.refundno like CONCAT('%','${refundno}','%' )


   	    and us.usercode like CONCAT('%','${usercode}','%' )


   	    and us.name like CONCAT('%','${username}','%' )


    and wr.refunddate >=#{refunddate,jdbcType=TIMESTAMP}


    and wr.refunddate <=#{refunddateend,jdbcType=TIMESTAMP}


     order by wr.userid


     order by wr.amount


     order by wr.type


     order by wr.refundno


     order by wr.refunddate


     order by wr.status


     order by us.usercode


     order by us.name


     desc


     asc

SELECT wr.id, wr.userid, us.usercode, us. NAME AS username, wr.refundno, w.balance AS EWallet, wr.type, wr.amount, wr.refunddate, wr.walletid, wr.remark, wr. STATUS, wr.confirmdate, wr.confirmuserid, wr.delflag, wr.capitalid, wr.orderdate, REPLACE ( REPLACE (wr.capitalvalue, ' ', ''), '\t', '' ) capitalvalue, us.documenttype, us.documentcode, us.defaultmobile, us.defaultmail FROM wallet_refund wr, `user` us, wallet_money w WHERE us.id = wr.userid AND wr.delflag = '0' AND w.userid = us.id
and wr.userid =#{userid,jdbcType=VARCHAR}
and wr.amount =#{amount,jdbcType=DECIMAL}
and wr.type =#{type,jdbcType=CHAR}
and wr.status =#{status,jdbcType=CHAR}
and wr.refundno like CONCAT('%','${refundno}','%' )
and us.usercode like CONCAT('%','${usercode}','%' )
and us.name like CONCAT('%','${username}','%' )
and wr.refunddate >=#{refunddate,jdbcType=TIMESTAMP}
and wr.refunddate <=#{refunddateend,jdbcType=TIMESTAMP}
order by wr.userid
order by wr.amount
order by wr.type
order by wr.refundno
order by wr.refunddate
order by wr.status
order by us.usercode
order by us.name
desc
asc



