 and applytime <=#{applytimeend,jdbcType=TIMESTAMP}


 and usercode =#{usercode,jdbcType=INTEGER}


 and paystatus =#{paystatus,jdbcType=INTEGER}


 and entitytype =#{entitytype,jdbcType=VARCHAR}

