mapper.select:
    SELECT *
	FROM user_mobile
	WHERE userid = #{userid,jdbcType=VARCHAR}
		AND telephone = #{numble,jdbcType=VARCHAR}

mapper.update:
UPDATE user_mobile
SET telephone=#{numble,jdbcType=VARCHAR}
WHERE id = #{id,jdbcType=VARCHAR}

mapper.select:
  	SELECT status = '1'
	FROM user_mobile
	WHERE userid = #{userid,jdbcType=VARCHAR} 
		AND telephone = #{numble,jdbcType=VARCHAR}

mapper.update:
  	UPDATE user_mobile
	SET STATUS = '1'
	WHERE userid = #{userid,jdbcType=VARCHAR} 
		AND telephone = #{numble,jdbcType=VARCHAR}

  	UPDATE user_mobile
	SET STATUS = '0'
	WHERE userid = #{userid,jdbcType=VARCHAR}
		AND telephone = #{numble,jdbcType=VARCHAR}
