mapper.select:
SELECT wj.id, wj.type, ( CASE sb.sys_blong_thirdparty WHEN '5' THEN CONCAT( ( CASE sb.addtowallet WHEN '1' THEN '' ELSE '-' END ), wj.amount) ELSE wj.amount END ) AS amount, wj.occurdate AS operateTime, wj.bankbillno, wj.ipsbillno, wj.billno, ( CASE WHEN em.`name` IS NOT NULL THEN em.`name` WHEN u2.`name` IS NOT NULL THEN u2.`name` ELSE ( SELECT NULL FROM `user` WHERE id = wj.userid ) END ) AS updatebyName, ( CASE sb.sys_blong_thirdparty WHEN '1' THEN CONCAT( '入-支付宝-', sb.sys_cn_name ) WHEN '2' THEN CONCAT( '入-财付通-', sb.sys_cn_name ) WHEN '5' THEN CONCAT( ( CASE sb.addtowallet WHEN '1' THEN '入-' ELSE '出-' END ), sb.sys_cn_name ) ELSE CONCAT('入-', sb.sys_cn_name) END ) AS paymentMethod, u.`name` AS username, u.usercode userYTid, wj.fund_flow_status AS fundFlowStatus, wj.bankcar_code AS bankcarCode, wj.voucher_no AS voucherNO, IFNULL( wj.trade_remark, CONCAT(py.requestcode, ',') ) AS tradeRemark FROM wallet_journal wj LEFT JOIN `user` u2 ON u2.id = wj.updateby LEFT JOIN employee em ON em.id = wj.updateby LEFT JOIN pay_journal py ON py.billno = wj.billno LEFT JOIN sys_bank sb ON sb.sys__anonymity = wj.payment_method, `user` u WHERE 1 = 1 AND u.id = wj.userid
AND wj.userid=#{userid,jdbcType=VARCHAR}
AND EXISTS ( SELECT 1 FROM `user` WHERE id = wj.userid
AND defaultmobile=#{userMoblie,jdbcType=VARCHAR}
AND defaultmail=#{userEmail,jdbcType=VARCHAR}
AND `name` = #{userName,jdbcType=VARCHAR}
AND usercode=#{userCode,jdbcType=VARCHAR}
)
AND date_format(wj.occurdate, '%Y-%m-%d')>=#{queryStartTime,jdbcType=VARCHAR}
AND date_format(wj.occurdate, '%Y-%m-%d')<=#{queryEndTime,jdbcType=VARCHAR}
ORDER BY wj.occurdate DESC,sb.sys_blong_thirdparty DESC limit #{startRow},#{pageSize}

select

from wallet_journal
where billno = #{id,jdbcType=VARCHAR}
AND
userid =
#{userid,jdbcType=VARCHAR}
